export default {
  name: "journalCard",
  props: {
    journal_id: {
      type: String
    },
    updated: {
      type: Boolean
    }
  },
  data: function data() {
    return {
      cur_journal: {
        type: Object
      },
      journal_date: {
        type: String
      },
      journal_name: {
        type: String
      },
      journal_creator: {
        type: String
      },
      original_journals: {
        type: Object
      }
    };
  },
  created: function created() {
    this.original_journals = Object.keys(this.$journal_fixtures.shown);

    if (!this.original_journals.includes(this.journal_id)) {
      console.log("get update data"); // this.cur_journal = this.$journal_fixtures.update[this.journal_id]

      this.cur_journal = JSON.parse(window.localStorage.getItem("update_journals"))[this.journal_id];
    } else {
      // this.cur_journal = this.$journal_fixtures.shown[this.journal_id];
      this.cur_journal = JSON.parse(window.localStorage.getItem("origin_journals"))[this.journal_id]; // console.log(this.cur_journal);
    }

    this.journal_name = this.cur_journal.title;
    this.journal_date = this.cur_journal.date;
    this.journal_creator = this.cur_journal.creator;
  },
  methods: {
    ShowDetail: function ShowDetail(journalId) {
      console.log(journalId);
      this.$router.push("/journal/".concat(journalId));
    },
    getImgUrl: function getImgUrl() {
      if (this.updated) {
        return this.cur_journal.image;
      } else {
        var jimg = this.cur_journal.image;
        return require("../assets/journals/" + jimg + ".jpg");
      }
    },
    delClick: function delClick() {
      this.$emit('delData', this.journal_id);
      console.log(this.journal_id);
    }
  }
};
