import Vue from "vue";
export default {
  name: "CameraApi",
  components: {// VuePictureSwipe,
  },
  data: function data() {
    return {
      isCameraOpen: true,
      canvasHeight: 400,
      canvasWidth: 400,
      mode: "face",
      twocam: true,
      item: []
    };
  },
  created: function created() {
    var cnt = 0;
    navigator.mediaDevices.enumerateDevices().then(function (devices) {
      devices.forEach(function (device) {
        if (device.kind == "audioinput") {
          cnt++;
        }
      });
    });
    if (cnt <= 1) this.twocam = false;
  },
  mounted: function mounted() {
    this.startCameraStream(); // var switchIcon = this.$refs.canshowswitch;
    // console.log(switchIcon);
    // if (this.twocam == false) {
    //   switchIcon.style.display = "none";
    // }
  },
  methods: {
    startCameraStream: function startCameraStream() {
      var _this = this;

      var constraints;

      if (this.mode == "face") {
        constraints = {
          audio: false,
          video: {
            width: {
              ideal: 400
            },
            height: {
              ideal: 400
            },
            facingMode: "face"
          }
        };
      } else if (this.mode == "environment") {
        constraints = {
          audio: false,
          video: {
            width: {
              ideal: 400
            },
            height: {
              ideal: 400
            },
            facingMode: {
              exact: "environment"
            }
          }
        };
      }

      navigator.mediaDevices.getUserMedia(constraints).then(function (stream) {
        _this.$refs.camera.srcObject = stream;
      }).catch(function (error) {
        alert("Browser doesn't support or there is some errors." + error);
      });
    },
    stopCameraStream: function stopCameraStream() {
      // console.log("call stop");
      var tracks = this.$refs.camera.srcObject.getTracks();
      tracks.forEach(function (track) {
        track.stop();
      });
    },
    capture: function capture() {
      var FLASH_TIMEOUT = 50;
      var self = this;
      setTimeout(function () {
        var context = self.$refs.canvas.getContext("2d");
        context.drawImage(self.$refs.camera, 0, 0, self.canvasWidth, self.canvasHeight);
        var dataUrl = self.$refs.canvas.toDataURL("image/jpeg").replace("image/jpeg", "image/octet-stream");
        self.addToPhotoGallery(dataUrl);
        console.log(dataUrl);
      }, FLASH_TIMEOUT);
    },
    addToPhotoGallery: function addToPhotoGallery(dataURI) {
      this.item = dataURI;
    },
    returnAndSave: function returnAndSave() {
      this.stopCameraStream();

      if (this.item != null) {
        Vue.prototype.$photo = this.item;
      }

      this.$router.push("/journal/new");
    },
    close: function close() {
      this.stopCameraStream();
      this.$router.push("/journal/new");
    },
    switchCam: function switchCam() {
      this.stopCameraStream();

      if (this.mode == "environment") {
        this.mode = "face";
      } else {
        this.mode = "environment";
      }

      this.startCameraStream();
    }
  }
};
